insert into ricetta (id, nome, foto,portata,descrizione) values(nextval('ric_seq'), 'Pollo alle mandorle', '/images/piatti/polloAlleMandorle.jpg','secondo','Tipico della cucina casalinga del Guangdong, il pollo alle mandorle è una ricetta molto apprezzata in tutto il mondo che non manca mai nel menù dei ristoranti cinesi! Come da tradizione, i bocconcini di pollo sono arricchiti con germogli di bambù .');
